import React, { useState } from 'react'
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from 'react-native'
import {RadioButton} from 'react-native-paper'

export default function Question(props) {
    const [check, setCheck] = useState('')
    props.onAnswer(props.question.title, check)

    return (
        <View style={{flex: 1, alignItems: 'center', justifyContent:'center'}}>
            <Text style={styles.questionTitle}>{props.question.title}</Text>
            <RadioButton.Group
                onValueChange={(value) => setCheck(value)}
                value={check}
            >
            <FlatList 
                data = {props.question.options}
                renderItem = { ({item}) => (
                <View style={styles.answer}>
                    <RadioButton value={item.key}/>
                    <TouchableOpacity onPress={() => {setCheck(item.key)}}>
                        <Text>{item.key}</Text>
                    </TouchableOpacity>
                </View>
                )}
            />
            </RadioButton.Group>
        </View>
    )
}

const styles = StyleSheet.create({
    questionTitle: {
        paddingVertical: '5px',
        fontWeight: 'bold',
        color: '#3F448C'
    },
    answer: {
        flexDirection: 'row', 
        alignItems: 'center', 
        marginVertical: -4
    }
})
