import React from 'react'
import { View, Text, FlatList } from 'react-native'
import {RadioButton} from 'react-native-paper'

export default function Solution(props){
    return(
        <View>
            <Text>{props.solution.title}</Text>
            <RadioButton.Group
                value={
                    // (props.solution.choosedOption === ' ')?
                    // props.solution.rightOption : props.solution.choosedOption
                    props.solution.rightOption
                }
            >
            <FlatList 
                data = {props.solution.options}
                renderItem = { ({item}) => (
                <View style={{flexDirection: 'row', alignItems: 'center', marginBottom: -5}}>
                    <RadioButton 
                        value={item.key} 
                        color={
                            (props.solution.rightOption === props.solution.choosedOption && item.key === props.solution.choosedOption)?
                            'green' : 
                            (props.solution.rightOption !== props.solution.choosedOption && item.key === props.solution.choosedOption)?
                            'red' : 'blue'
                        }
                    />
                    <Text style={
                        (props.solution.choosedOption === '' && item.key === props.solution.rightOption)?
                        {color: 'blue'}:
                        (props.solution.rightOption === props.solution.choosedOption && item.key == props.solution.choosedOption)?
                        {color: 'green'} : 
                        (props.solution.rightOption !== props.solution.choosedOption && item.key == props.solution.choosedOption)?
                        {color: 'red'} : {color: 'black'}}
                    >
                        {item.key}
                    </Text>
                </View>
                )}
            />
            </RadioButton.Group>
        </View>
    )
}
