import React, { useState } from 'react'
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from 'react-native'
import {RadioButton} from 'react-native-paper'

export default function Question(props) {
    const [check, setCheck] = useState('')
    const handleChoice = (check) => props.onAnswer(check)
    // props.onAnswer(check)
    console.log('Question Screen '+ check)

    return (
        <View style={{flex: 1, alignItems: 'center', justifyContent:'center'}}>
            <Text>{props.question.title}</Text>
            <RadioButton.Group
                onValueChange={(value) => {
                    setCheck(value)
                    handleChoice(value)
                }}
                value={check}
            >
            <FlatList 
                data = {props.question.options}
                renderItem = { ({item}) => (
                <View style={{flexDirection: 'row', alignItems: 'center', marginBottom: -5}}>
                    <RadioButton value={item.key}/>
                    <TouchableOpacity onPress={() => {setCheck(item.key)}}>
                        <Text>{item.key}</Text>
                    </TouchableOpacity>
                </View>
                )}
            />
            </RadioButton.Group>
        </View>
    )
}
