import React from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
//import content from '../components/data'
import Card from '../components/Card'
import { useSelector } from 'react-redux'
import { useFirebase, useFirebaseConnect, useFirestore, useFirestoreConnect } from 'react-redux-firebase'

function AboutScreen({ navigation }) {
  //const firebase = useFirebase()
  const firestore = useFirestore()
  useFirestoreConnect('contents')
  // useFirebaseConnect([{path: 'flashcards'}])
  //const projects = useSelector(state => {console.log(firestore)})
  const projects = useSelector(state => {
    let first = state.firestore.ordered.contents
    console.log(first)
  })

  return (
    <FlatList 
      data={projects}
      numColumns={2}
      renderItem={({item}) => (
        <View style={{width: '50%'}}>
          <TouchableOpacity onPress={() =>{ navigation.navigate('Unit', item)}}>
            <Card>
              <View style={{height: 100}}>  
                <Text>{ item.title }</Text>
              </View>
            </Card>
          </TouchableOpacity>
        </View>
      )}
    />
  )
}

export default AboutScreen

const styles = StyleSheet.create({
  title: {
    fontWeight: 'bold',
    color: '#3F448C'
  }
})
