import React, {useState} from 'react'
import {View, Text, FlatList, StyleSheet} from 'react-native'
import Card from '../components/Card'
// import Unit1 from '../components/Unit1'
import db from '../services/firebase'

export default function ExplanationsScreen(){
    const [Unit1, setUnit1] = useState([])
    db.collection('contents').doc('unit_1').get().then((snapshot) => {
        setUnit1(snapshot.data().explanation)
    })
    const displayText = (text) => {
        if(text.includes('<b>')){
        return(
            <Text>
                {text.split('<b>').map((value, index) => {
                    return (index % 2 == 0) ? 
                        <Text>{value}</Text> :
                        <Text style={styles.bold}>{value}</Text>
                })}
            </Text>
        )}
        else if(text.includes('<i>')){
        return(
            <Text>
                {text.split('<i>').map((value, index) => {
                    return (index % 2 == 0) ? 
                        <Text>{value}</Text> :
                        <Text style={styles.italic}>{value}</Text>
                })}
            </Text>
        )}
        else if(text.includes('<u>')){
        return(
            <Text>
                {text.split('<u>').map((value, index) => {
                    return (index % 2 == 0) ? 
                        <Text>{value}</Text> :
                        <Text style={styles.underline}>{value}</Text>
                })}
            </Text>
        )}
        else 
        return(
            <Text>{text}</Text>
        )
    }

    return(
        <View style={{flex: 1, alignItems: 'center', justifyContent:'center'}}>
            <FlatList 
                data = {Unit1}
                renderItem = {({item}) =>
                    <Card>
                        <Text style={{fontWeight: 'bold', color: '#3F448C'}}>{item.title}</Text>
                        {item.sub_explanation.map((value) => {
                            return(
                            <View>
                                <Text>{value.title}</Text>
                                {value.examples.map((value) => {
                                    return(
                                        <Text style={styles.italic}>
                                        {displayText('      '+value)}
                                        </Text>
                                    )
                                })}
                            </View>
                            )
                        })}
                    </Card>
                }
            />
        </View>
    )
}

const styles = StyleSheet.create({
    bold: {fontWeight: 'bold'},
    italic: {fontStyle: 'italic'},
    underline: {textDecorationLine: 'underline'}
})
