import React, { useState } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
//import content from '../components/data'
import Card from '../components/Card'
import { useSelector } from 'react-redux'
import { useFirebase, useFirebaseConnect, useFirestore, useFirestoreConnect } from 'react-redux-firebase'
import FlashcardsScreenPractice from '../screens/FlashcardsPracticeScreen'

function AboutScreen({ navigation }) {
  const firestore = useFirestore() 
  useFirestoreConnect('/flashcards/contents')
  const projects = useSelector(state => {
    console.log(state.firestore.data.flashcards.contents)
  })
  // const projects = useSelector((state) => state.project.projects)

  return (
    <FlatList 
      data={projects}
      numColumns={2}
      renderItem={({item}) => (
        <View style={{width: '50%'}}>
          <TouchableOpacity onPress={() =>{ navigation.navigate('FlashcardsPracticeScreen', item)}}>
            <Card>
              <View style={{height: 100}}>  
                <Text>{ item.title }</Text>
              </View>
            </Card>
          </TouchableOpacity>
        </View>
      )}
    />
  )
}

export default AboutScreen

const styles = StyleSheet.create({
  title: {
    fontWeight: 'bold',
    color: '#3F448C'
  }
})
