import React from 'react'
import {View, Text, StyleSheet} from 'react-native'

export default function ExplanationsScreen(){
    const text = 'This is <b>bold<b> text'
    function specializedText(text){
        const splittedText = text.split('<b>')
        return(
            <Text>specializedText[0]
            <Text>{specializedText[1]}</Text>
            specializedText[2]
            </Text>
        )
    }

    return(
        <View style={{flex: 1, alignItems: 'center', justifyContent:'center'}}>
            <Text>{'This is '}
                <Text style={styles.bold}>Bold text</Text>
            </Text>
            <Text style={styles.italic}>Italic text</Text>
            <Text style={styles.underline}>Underlined Text</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    bold: {fontWeight: 'bold'},
    italic: {fontStyle: 'italic'},
    underline: {textDecorationLine: 'underline'}
})
