import React from 'react'
import { View, FlatList, Text } from 'react-native'
import Card from '../components/Card'

export default function FlashcardsPracticeScreen(){
    const flashcards = [
        {term: 'term 1', def: 'def 1'},
        {term: 'term 2', def: 'def 2'},
        {term: 'term 3', def: 'def 3'},
    ]

    const terms = []
    const defs = []
    flashcards.forEach((item) => {
        terms.push(item.term)
        defs.push(item.def)
    })

    return(
        <View style={{flexDirection: 'row'}}>
            <View style={{width: '50%'}}>
                <FlatList
                    data={flashcards}
                    renderItem={({item}) => (
                        <View>
                            <Card><Text>{item.term}</Text></Card>
                        </View>
                    )}
                />
            </View>
            <View style={{width: '50%'}}>
                <FlatList
                    data={flashcards}
                    renderItem={({item}) => (<View><Text>{item.def}</Text></View>)}
                />
            </View>
        </View>
    )
}
