import React, { useState } from 'react'
import { FlatList, Button, View } from 'react-native'
import Question from '../components/Question'
import Solution from '../components/Solution'
import Card from '../components/Card'
import db from '../services/firebase'
// import questions from '../components/questions'

export default function ExercisesScreen(){
    const [checkedAnswers, setCheckedAnswers] = useState([])
    const [isSubmitAnswer, setSubmitAnswer] = useState(false)
    const [questions, setQuestions] = useState([])
    const key = 'unit_1'
    
    db.collection('contents').doc(key).get().then((snapshot) => {
        setQuestions(snapshot.data().questions)
    })

    const answers = questions.map((question) => {
        return {...question, choosedOption: ''}
    })
    const handleAnswer = (title, checked) => {
        answers.forEach((answer) => {
            if(answer.title === title){
                answer.choosedOption = checked
            }
        })
        console.log('Exercise Screen: '+title+ ' ' +checked)
        console.log(answers)
        setCheckedAnswers(answers)
    }
    
    if(!isSubmitAnswer){
        return(
            <View>
                <FlatList 
                    data = {questions}
                    renderItem = {({item}) =>
                        <Card>
                            <Question question={item} onAnswer={handleAnswer}/>    
                        </Card>
                    }
                />
                <Button title='Submit' onPress={() => {setSubmitAnswer(true)}}/>    
            </View>
        )
    }
    return(
        <View>
            <FlatList 
                data = {checkedAnswers}
                renderItem = {({item}) =>
                    <Card>
                        <Solution solution={item}/>    
                    </Card>
                }
            />
            <Button title='Redo' onPress={() => setSubmitAnswer(false)}/>     
        </View>
    )
}
